class KernelRidge(BaseEstimator, RegressorMixin):
    def __init__(self, alpha=1, kernel="linear", gamma=None, degree=3, coef0=1, kernel_params=None):
        self.alpha = alpha
        self.kernel = kernel
        self.gamma = gamma
        self.degree = degree
        self.coef0 = coef0
        self.kernel_params = kernel_params

    def _get_kernel(self, X, Y=None):
        if callable(self.kernel):
            params = self.kernel_params or {}
        else:
            params = {"gamma": self.gamma, "degree": self.degree, "coef0": self.coef0}
        return pairwise_kernels(X, Y, metric=self.kernel, filter_params=True, **params)

    def fit(self, X, y):
        X, y = check_X_y(X, y)
        K = self._get_kernel(X)
        alpha_ = np.linalg.solve(K + self.alpha * np.eye(X.shape[0]), y)
        self.X_fit_ = X
        self.alpha_ = alpha_
        return self

    def predict(self, X):
        check_is_fitted(self, ["X_fit_", "alpha_"])
        K = self._get_kernel(X, self.X_fit_)
        return safe_sparse_dot(K, self.alpha_)

    def _more_tags(self):
        return {
            "_xfail_checks": {
