        self.distances[(from_node, to_node)] = distance

    @rename_kwargs('start', 'end')
    def dijkstra(self, start_f, end_f):
        queue = [(0, start_f, [])]
        seen = set()
        while queue:
            (cost, node, path) = heapq.heappop(queue)
            if node not in seen:
                seen.add(node)
                path = path + [node]
                if node == end_f:
                    return cost, path
                for neighbor in self.edges[node]:
                    if neighbor not in seen:
                        heapq.heappush(queue, (cost + self.distances[(node, neighbor)], neighbor, path))
        return float("inf")

    def shortest_path(self, start, end):
        return self.dijkstra(start, end)
