def o_is_valid_sudoku(vr_board):
    """
    This function checks if a given 2D array represents a valid Sudoku puzzle.

    A valid Sudoku puzzle is one where each row, column, and 3x3 sub-grid contains 
    the numbers 1-9 without repetition.

    Parameters:
    vr_board (list): A 2D list representing the Sudoku puzzle.

    Returns:
    bool: True if the Sudoku puzzle is valid, False otherwise.
    """

    # Check each row for validity
    for vr_row in vr_board:
        vr_seen = set()
        for vr_num in vr_row:
            if vr_num != 0 and vr_num in vr_seen:
                return False
            vr_seen.add(vr_num)

    # Check each column for validity
    for vr_col in range(9):
        vr_seen = set()
        for vr_row in range(9):
            vr_num = vr_board[vr_row][vr_col]
            if vr_num != 0 and vr_num in vr_seen:
                return False
            vr_seen.add(vr_num)

    # Check each 3x3 sub-grid for validity
    for vr_i in range(0, 9, 3):
        for vr_j in range(0, 9, 3):
            vr_seen = set()
            for vr_x in range(3):
                for vr_y in range(3):
                    vr_num = vr_board[vr_i + vr_x][vr_j + vr_y]
                    if vr_num != 0 and vr_num in vr_seen:
                        return False
                    vr_seen.add(vr_num)

    # If all checks pass, the Sudoku puzzle is valid
    return True
