from pedantic import timer

class BinaryTree:
    """A basic binary tree class with methods to insert and delete nodes."""

    def __init__(self):
        """Initialize the binary tree with an empty root node."""
        self.root = None

    @timer
    def insert(self, value):
        """Insert a new node with the given value into the binary tree.

        Args:
            value (any): The value to be inserted into the binary tree.
        """
        if not self.root:
            self.root = Node(value)
        else:
            self._insert_recursive(self.root, value)

    def _insert_recursive(self, node, value):
        """Recursively insert a new node with the given value into the binary tree.

        Args:
            node (Node): The current node in the binary tree.
            value (any): The value to be inserted into the binary tree.
        """
        if value < node.value:
            if node.left:
                self._insert_recursive(node.left, value)
            else:
                node.left = Node(value)
        else:
            if node.right:
                self._insert_recursive(node.right, value)
            else:
                node.right = Node(value)

    @timer
    def delete(self, value):
        """Delete the node with the given value from the binary tree.

        Args:
            value (any): The value of the node to be deleted from the binary tree.
        """
        self.root = self._delete_recursive(self.root, value)

    def _delete_recursive(self, node, value):
        """Recursively delete the node with the given value from the binary tree.

        Args:
            node (Node): The current node in the binary tree.
            value (any): The value of the node to be deleted from the binary tree.

        Returns:
            Node: The updated node after deletion.
        """
        if not node:
            return node
        if value < node.value:
            node.left = self._delete_recursive(node.left, value)
        elif value > node.value:
            node.right = self._delete_recursive(node.right, value)
        else:
            if not node.left and not node.right:
                return None
            elif not node.left:
                return node.right
            elif not node.right:
                return node.left
            else:
                min_node = self._find_min(node.right)
                node.value = min_node.value
                node.right = self._delete_recursive(node.right, min_node.value)
        return node

    def _find_min(self, node):
        """Find the node with the minimum value in the binary tree.

        Args:
            node (Node): The current node in the binary tree.

        Returns:
            Node: The node with the minimum value in the binary tree.
        """
        while node.left:
            node = node.left
        return node


