class n_KernelRidgeRegression:
    def __init__(self, alpha=1.0, kernel='linear', gamma=None):
        self.alpha = alpha
        self.kernel = kernel
        self.gamma = gamma
        self.X_fit = None
        self.y_fit = None

    def n_fit(self, X, y):
        self.X_fit = X
        self.y_fit = y
        return self

    def n_predict(self, X):
        if self.kernel == 'linear':
            K = self.n_linear_kernel(X, self.X_fit)
        elif self.kernel == 'rbf':
            K = self.n_rbf_kernel(X, self.X_fit)
        else:
            raise ValueError("Invalid kernel type")

        return self.n_solve(K, self.y_fit)

    def n_linear_kernel(self, X, Y):
        return X @ Y.T

    def n_rbf_kernel(self, X, Y):
        sqdist = self.n_squared_dist(X, Y)
        return np.exp(-self.gamma * sqdist)

    def n_squared_dist(self, X, Y):
        return np.sum(X**2, axis=1)[:, np.newaxis] + np.sum(Y**2, axis=1) - 2 * X @ Y.T

    def n_solve(self, K, y):
        n_samples = K.shape[0]
        K.flat[::n_samples + 1] += self.alpha
        return np.linalg.solve(K, y)
