def vr_is_valid_sudoku(vr_board):
    """
    Determines if a given 2D array represents a valid Sudoku board.

    A valid Sudoku board is one where each row, column, and 3x3 sub-grid contains
    the numbers 1-9 without repetition.

    Args:
        vr_board (list): A 2D list representing the Sudoku board.

    Returns:
        bool: True if the board is valid, False otherwise.
    """

    # Check if the board is a 9x9 grid
    if len(vr_board) != 9 or any(len(row) != 9 for row in vr_board):
        return False

    # Check rows for validity
    for vr_row in vr_board:
        vr_seen = set()
        for vr_num in vr_row:
            if vr_num < 1 or vr_num > 9 or vr_num in vr_seen:
                return False
            vr_seen.add(vr_num)

    # Check columns for validity
    for vr_col in range(9):
        vr_seen = set()
        for vr_row in range(9):
            vr_num = vr_board[vr_row][vr_col]
            if vr_num < 1 or vr_num > 9 or vr_num in vr_seen:
                return False
            vr_seen.add(vr_num)

    # Check 3x3 sub-grids for validity
    for vr_box_row in range(0, 9, 3):
        for vr_box_col in range(0, 9, 3):
            vr_seen = set()
            for vr_row in range(3):
                for vr_col in range(3):
                    vr_num = vr_board[vr_box_row + vr_row][vr_box_col + vr_col]
                    if vr_num < 1 or vr_num > 9 or vr_num in vr_seen:
                        return False
                    vr_seen.add(vr_num)

    # If all checks pass, the board is valid
    return True
