class BinarySearchTree:
    """Represents a binary search tree."""

    def __init__(self):
        """Initializes an empty binary search tree."""

        self.root = None


    def delete_node(self, value):
        """Deletes a node with a given value from the binary search tree."""

        self.root = self._delete_node_recursive(self.root, value)


    def _delete_node_recursive(self, node, value):
        """Recursively deletes a node with a given value from the binary search tree."""

        if node is None:
            return node

        if value < node.value:
            node.left = self._delete_node_recursive(node.left, value)
        elif value > node.value:
            node.right = self._delete_node_recursive(node.right, value)
        else:
            # Node to delete found
            if node.left is None:
                return node.right
            elif node.right is None:
                return node.left

            # Node has two children
            temp = self._min_value_node(node.right)
            node.value = temp.value
            node.right = self._delete_node_recursive(node.right, temp.value)

        return node


    def _min_value_node(self, node):
        """Finds the node with the minimum value in a given subtree."""

        current = node
        while current.left is not None:
            current = current.left
        return current
