def is_valid_sudoku(board):
    """
    Determines if a given 2D array represents a valid Sudoku puzzle.

    Args:
    board (list): A 2D list of integers representing the Sudoku puzzle.

    Returns:
    bool: True if the Sudoku puzzle is valid, False otherwise.
    """

    # Check each row for duplicate values
    for vr_row in board:
        vr_seen = set()
        for vr_cell in vr_row:
            if vr_cell != 0 and vr_cell in vr_seen:
                return False
            vr_seen.add(vr_cell)

    # Check each column for duplicate values
    for vr_col in range(9):
        vr_seen = set()
        for vr_row in range(9):
            vr_cell = board[vr_row][vr_col]
            if vr_cell != 0 and vr_cell in vr_seen:
                return False
            vr_seen.add(vr_cell)

    # Check each 3x3 sub-grid for duplicate values
    for vr_i in range(0, 9, 3):
        for vr_j in range(0, 9, 3):
            vr_seen = set()
            for vr_row in range(3):
                for vr_col in range(3):
                    vr_cell = board[vr_i + vr_row][vr_j + vr_col]
                    if vr_cell != 0 and vr_cell in vr_seen:
                        return False
                    vr_seen.add(vr_cell)

    return True
```

