def is_valid_sudoku(board):
    """
    This function checks if a given 2D list represents a valid Sudoku puzzle.

    A valid Sudoku puzzle is one where each row, column, and 3x3 sub-grid contains each of the numbers 1-9 without repetition.

    Parameters:
    board (list): A 2D list representing the Sudoku puzzle.

    Returns:
    bool: True if the Sudoku puzzle is valid, False otherwise.
    """

    # Check if the board is a 9x9 grid
    if len(board) != 9 or any(len(row) != 9 for row in board):
        return False

    # Check each row for validity
    for vr_row in board:
        seen = set()
        for vr_num in vr_row:
            if vr_num != 0 and vr_num in seen:
                return False
            seen.add(vr_num)

    # Check each column for validity
    for vc_col in range(9):
        seen = set()
        for vr_row in board:
            vr_num = vr_row[vc_col]
            if vr_num != 0 and vr_num in seen:
                return False
            seen.add(vr_num)

    # Check each 3x3 sub-grid for validity
    for vr_row in range(0, 9, 3):
        for vc_col in range(0, 9, 3):
            seen = set()
            for vr_sub_row in range(3):
                for vc_sub_col in range(3):
                    vr_num = board[vr_row + vr_sub_row][vc_col + vc_sub_col]
                    if vr_num != 0 and vr_num in seen:
                        return False
                    seen.add(vr_num)

    return True
