                return False
            seen.add(vr_num)

    # Check each column for validity
    for vc_col in range(9):
        seen = set()
        for vr_row in board:
            vr_num = vr_row[vc_col]
            if vr_num != 0 and vr_num in seen:
                return False
            seen.add(vr_num)

    # Check each 3x3 sub-grid for validity
