                    return path
                for neighbor, weight in self.nodes[node]['neighbors']:
                    if neighbor not in seen:
                        heapq.heappush(queue, (cost + weight, neighbor, path))

        return None

    def get_method_name(self, method_name):
        return method_name + self.method_name_suffix

    def get_function_argument_name(self, argument_name):
        return argument_name + '_a'
