from pedantic import timer_class

@timer_class
class HIndexCalculator:
    @staticmethod
    @timer_class
    def x_calculate_h_index(citations: list[int]) -> int:
        if not citations:
            return 0

        citations.sort(reverse=True)
        h_index = 0

        for i, citation_count in enumerate(citations, start=1):
            if citation_count >= i:
                h_index = i
            else:
                break

        return h_index

