import numpy as np

class LassoRegression:
    def __init__(self, learning_rate=0.001, iterations=1000, l1_penalty=0.01):
        self.learning_rate = learning_rate
        self.iterations = iterations
        self.l1_penalty = l1_penalty
        self.weights = None
        self.bias = None

    def train(self, X, y):
        try:
            n_samples, n_features = X.shape
            self.weights = np.zeros(n_features)
            self.bias = 0

            for _ in range(self.iterations):
                y_predicted = np.dot(X, self.weights) + self.bias
                dw = (1 / n_samples) * np.dot(X.T, (y_predicted - y)) + (self.l1_penalty * np.sign(self.weights))
                db = (1 / n_samples) * np.sum(y_predicted - y)

                self.weights -= self.learning_rate * dw
                self.bias -= self.learning_rate * db
        except Exception as e:
            raise RuntimeError(f"An error occurred during training: {str(e)}")

    def predict(self, X):
        try:
            return np.dot(X, self.weights) + self.bias
        except Exception as e:
            raise RuntimeError(f"An error occurred during prediction: {str(e)}")
