import numpy as np

class LassoRegression:
    def __init__(self, alpha=1.0, max_iter=1000, tol=1e-4):
        self.alpha = alpha
        self.max_iter = max_iter
        self.tol = tol
        self.coef_ = None

    def _soft_threshold(self, rho, alpha):
        if rho < -alpha:
            return rho + alpha
        elif rho > alpha:
            return rho - alpha
        else:
            return 0.0

    def train(self, X, y):
        try:
            n_samples, n_features = X.shape
            self.coef_ = np.zeros(n_features)
            weights = self.coef_

            for iteration in range(self.max_iter):
                weights_old = np.copy(weights)

                for j in range(n_features):
                    temp = weights.copy()
                    temp[j] = 0.0
                    residual = y - X @ temp
                    rho = np.dot(X[:, j], residual)

                    weights[j] = self._soft_threshold(rho, self.alpha)

                if np.sum(np.abs(weights - weights_old)) < self.tol:
                    break
        except Exception as e:
            print(f"An error occurred during training: {e}")

    def predict(self, X):
        try:
            if self.coef_ is None:
                raise ValueError("Model coefficients are not initialized. Call `train` method first.")
            return np.dot(X, self.coef_)
        except Exception as e:
            print(f"An error occurred during prediction: {e}")
