def c_merge_sort_a(arr):
    def c_merge_a(left, right):
        sorted_arr = []
        i = j = 0
        while i < len(left) and j < len(right):
            if left[i] < right[j]:
                sorted_arr.append(left[i])
                i += 1
            else:
                sorted_arr.append(right[j])
                j += 1
        sorted_arr.extend(left[i:])
        sorted_arr.extend(right[j:])
        return sorted_arr

    def c_sort_a(sub_arr):
        if len(sub_arr) <= 1:
            return sub_arr
        mid = len(sub_arr) // 2
        left = c_sort_a(sub_arr[:mid])
        right = c_sort_a(sub_arr[mid:])
        return c_merge_a(left, right)

    return c_sort_a(arr)
